import React from 'react';
import moi from "../src/moi.jpg"
import './App.css';
let i = 0;
class App extends React.Component {
  state ={fullName:"Yassine Seltene",bio:"Je suis un développeur Web fullstack js", 
  profession:"ingénieur développeur web", isShow:true}
  hide=()=>this.setState({isShow: !this.state.isShow }); 

  componentDidMount(){
  setInterval()((i)=>{++i}, 1000)}
  render(){

    return (
    <div className="App">
    <button onClick={this.hide}> Hide </button>
      {this.state.isShow && (<div><h2> My name is: {this.state.fullName}</h2>
      <h2> My bio : {this.state.bio}</h2>
      <p> <img src={moi} alt={this.state.fullName}/> </p>
      <h2> My profession {this.state.profession}</h2></div>)}

    </div>
  );
}}

export default App;
